mapper.update:
update tb_qna
   set qna_view_cnt = qna_view_cnt + 1
 where qna_no = #{qnaNo}

mapper.select:
select qna_view_cnt
  from tb_qna
 where qna_no = #{qnaNo}

	    select *
	      from (select rownum rnum, a.*
			      from ( 
					      select q.*, (select user_id  from tb_user u where u.user_no = q.user_no ) as user_id 
                            from tb_qna q
						   order by qna_no desc
						   ) a  )			   
		 where rnum between #{begin} and #{end}

		select q.*, (select user_id  from tb_user u where u.user_no = q.user_no ) as user_id 
                            from tb_qna q
			where qna_no = #{qnaNo}

mapper.insert:
insert into tb_qna (
	qna_no, qna_title, qna_content, user_no, qna_public_enabled
) values (
	tb_qna_seq.nextval, #{qnaTitle}, #{qnaContent}, #{userNo}, #{qnaPublicEnabled}
)

mapper.update:
update tb_qna 
   set qna_title = #{qnaTitle}, 
       qna_content = #{qnaContent}
 where qna_no = #{qnaNo}

update tb_qna 
   set qna_view_cnt = qna_view_cnt + 1
 where qna_no = #{qnaNo}

update tb_comment
	set cmt_content = #{cmtContent}
	where cmt_no = #{cmtNo}
