mapper.select:
		select *
	      from (select rownum rnum, a.*
			      from ( 
					      select n.*, (select user_id  from tb_user u where u.user_no = n.user_no ) as user_id 
                            from tb_talk n
						   order by talk_post_no desc
						  )
						  a)
		 where rnum between #{begin} and #{end}

mapper.insert:
insert into tb_talk (
	no, title, writer, content
) values (
	seq_talk_no.nextval, #{title}, #{writer}, #{content}
)

mapper.update:
update tb_talk 
   set view_cnt = view_cnt + 1
 where no = #{no}

update tb_talk 
   set title = #{title}, 
       content = #{content}
 where no = #{no}

mapper.insert:
insert into tb_comment(
	comment_no, no, writer, content
) values (
	seq_comment_no.nextVal, #{no}, #{writer}, #{content}
)

mapper.update:
update tb_comment
   set content = #{content}
 where comment_no = #{commentNo}
